SELECT * FROM (SELECT @ROWNUM := @ROWNUM +1 ROWNUM, Q.* FROM QNA Q) S WHERE ROWNUM 
BETWEEN ${param1}*${param2}-(${param2}-1) AND ${param1}*${param2}

